	private void generatePopulation() {
		
			
				 double factor = 0;
				//SPNtoSPN				
				factor = 1; 
				SPNtoSPN =(int) (0.5* Zone.SPN.workTripsToSPNPerDay);
				CBtoCB = (int) (0.5* Zone.CB.workTripsToCBPerDay);
				factor = ((double)Zone.CB.workingPopulation * (double)Zone.SPN.workplaces)/((double)Zone.SPN.workingPopulation *	(double)Zone.CB.workplaces );
				SPNtoCB =  (int) Math.round(0.5*(Zone.SPN.workTripsToCBPerDay*factor)/(1+factor));
				CBtoSPN = (int) (0.5*Zone.SPN.workTripsToCBPerDay-SPNtoCB);
				System.out.println(CBtoSPN);
				System.out.println(SPNtoCB);
				double sizedown = 0.1;
				SPNtoCB = (int) Math.round(sizedown*SPNtoCB);
				CBtoSPN = (int) Math.round(sizedown*CBtoSPN);
				SPNtoSPN = (int) Math.round(sizedown*SPNtoSPN);
				CBtoCB = (int) Math.round(sizedown*CBtoCB);
				System.out.println(CBtoSPN);
				System.out.println(SPNtoCB);
				System.out.println(CBtoCB);
				System.out.println(SPNtoSPN);
				
				int pnr=0;
				for (int i = 0; i<=SPNtoSPN;i++){
					generatePlanForZones(pnr,Zone.SPN,Zone.SPN);
					pnr++;
				}
				for (int i = 0; i<=SPNtoCB;i++){
					generatePlanForZones(pnr,Zone.SPN,Zone.CB);
					pnr++;
				}
				for (int i = 0; i<=CBtoCB;i++){
					generatePlanForZones(pnr,Zone.CB,Zone.CB);
					pnr++;
				}
				for (int i = 0; i<=CBtoSPN;i++){
					generatePlanForZones(pnr,Zone.CB,Zone.SPN);
					pnr++;
				}
				System.out.println("h-w-h-Plans created: "+pnr);
				int fans = 0;
				for (int i=0; i<1000; i++){
					generateFootBallNerd(fans, Zone.SPN);
					fans++;
				}
				for (int i=0; i<500; i++){
					generateFootBallNerd(fans, Zone.CB);
					fans++;
				}
				System.out.println("h-Football-h-Plans created: "+fans);
				System.out.println("total Plans created: "+(pnr+fans));


					
				
		
	}
	
	private void generateFootBallNerd(int fnr, Zone home){
		double offset = 3600 * Math.random();
		double start = 17*3600 + offset;
		double end = 19.75*3600 + offset;
		int fans = fnr;
		Zone homeZone = home;
		Person p;
		p = generatePerson(homeZone,Zone.SDF,start,end,fans);
		population.addPerson(p);
	
		
		
	}
	
	private void generatePlanForZones(int pnr, Zone home, Zone work ){
		double offset = 14400 * Math.random();
		double start = 6 * 3600 + offset;
		double end = 14.5*3600 + offset;
		int nr;
		nr= pnr;
		Person p;;
		Zone homeZone = home;
		Zone workZone = work;
		p = generatePerson(homeZone, workZone, start, end, nr);
		population.addPerson(p);
		
		
	}
	
	private Person generatePerson(Zone home, Zone work, double workStart, double workEnd, int pnr){
		Person p;
		Id id;
		Plan plan;
		id = scenario.createId(pnr+"_"+home.toString()+"_"+work.toString());
		p = population.getFactory().createPerson(id);
		plan = generatePlan(home,work,workStart,workEnd);
		p.addPlan(plan);
		return p;
	}
	private Plan generatePlan(Zone home, Zone work, double workStart, double workEnd){
		Plan plan= population.getFactory().createPlan(); 
		
		Coord homeCoord = this.shoot(home); 
		Coord workCoord = this.shoot(work);
		if (work==Zone.SDF){
			plan.addActivity(this.addActivity(home, "home", 0.0, workStart-1.0, homeCoord));
			plan.addLeg(this.addLeg(workStart -1)); 
			plan.addActivity(this.addActivity(work, "fb",	workStart, workEnd, workCoord)); 
			plan.addLeg(this.addLeg(workEnd));
			plan.addActivity(this.addActivity(home, "home", workEnd + 1.0, 24.0 * 3600, homeCoord));
			
			
		} else{

		plan.addActivity(this.addActivity(home, "home", 0.0, workStart-1.0, homeCoord));
		plan.addLeg(this.addLeg(workStart -1)); 
		plan.addActivity(this.addActivity(work, "work",	workStart, workEnd, workCoord)); 
		plan.addLeg(this.addLeg(workEnd));
		plan.addActivity(this.addActivity(home, "home", workEnd + 1.0, 24.0 * 3600, homeCoord));}
		return plan;
		
	}
	
	private Activity addActivity(Zone zone, String type, Double start, Double end, Coord coord){
	
	Activity activity = population.getFactory().createActivityFromCoord(type, coord);
	activity.setStartTime(start);
	activity.setEndTime(end);
	return activity;
	}
	
	private Leg addLeg (double departure){ 
		Leg leg = population.getFactory().createLeg("car");
		leg.setDepartureTime(departure+1);
	return leg;
	}
	
	private Coord shoot(Zone zone) {
		Random r = new Random();
		Point point;
		if (zone==Zone.SPN){
			do {
			point = getRandomPointInFeature(r, zoneGeometries.get(zone));
			} 
				while (zoneGeometries.get(Zone.CB).covers(point));
				//verifies SPN point is not in CB
		}
		else 	point = getRandomPointInFeature(r, zoneGeometries.get(zone));

		
		CoordImpl coordImpl = new CoordImpl(point.getX(), point.getY());
		return ct.transform(coordImpl);
	}

	private static Point getRandomPointInFeature(Random rnd, Geometry g) {
		Point p = null;
		double x, y;
		do {
			x = g.getEnvelopeInternal().getMinX() + rnd.nextDouble() * (g.getEnvelopeInternal().getMaxX() - g.getEnvelopeInternal().getMinX());
			y = g.getEnvelopeInternal().getMinY() + rnd.nextDouble() * (g.getEnvelopeInternal().getMaxY() - g.getEnvelopeInternal().getMinY());
			p = MGC.xy2Point(x, y);
		} while (!g.contains(p));
		return p;
	}

